// Home.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser } from './UserContext';

const Home = () => {
  const { userEmail, deptId } = useUser(); // Now getting both userEmail and deptId
  const navigate = useNavigate();

  const handleAccessDocument = () => {
    navigate('/access', {
      state: { userEmail, deptId }, // Pass both to access page
    });
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#1f2937', color: 'white' }}>
      <h1>Welcome {userEmail}</h1>

      <button
        onClick={handleAccessDocument}
        style={{
          padding: '12px',
          backgroundColor: '#6366f1',
          color: 'white',
          border: 'none',
          borderRadius: '6px',
          fontWeight: '500',
          cursor: 'pointer',
          marginTop: '20px',
        }}
      >
        Access Document
      </button>
    </div>
  );
};

export default Home;
