import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const AccessPage = () => {
  const { state } = useLocation();
  const [accessId, setAccessId] = useState('');
  const [message, setMessage] = useState('');
  const [deptInfo, setDeptInfo] = useState(null);

  const userEmail = state?.userEmail;
  const deptId = state?.deptId;

  console.log("Received deptId:", deptId);
  console.log("Received userEmail:", userEmail);

  useEffect(() => {
    const fetchDepartmentInfo = async () => {
      try {
        const response = await fetch(`http://localhost:5000/get-department-info/${deptId}`);
        const data = await response.json();

        console.log("Fetched department info:", data);

        if (data.success) {
          setDeptInfo(data.departmentInfo);
        } else {
          setMessage(data.message);
        }
      } catch (err) {
        console.error("Error fetching department info:", err);
        setMessage('Error fetching department info');
      }
    };

    if (deptId) fetchDepartmentInfo();
  }, [deptId]);

  const handleAccessCheck = (e) => {
    e.preventDefault();

    console.log("Entered Access ID:", accessId);

    if (!deptInfo) {
      setMessage('Department information not loaded yet.');
      return;
    }

    const isAuthorized = deptInfo.Access_ID.includes(parseInt(accessId));
    console.log("Access ID array from deptInfo:", deptInfo.Access_ID);
    console.log("Is user authorized?", isAuthorized);

    if (isAuthorized) {
      setMessage(`Access granted to ${deptInfo.Dept_Name} documents.`);
    } else {
      setMessage('Access denied: Invalid Access ID.');
    }
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#1f2937', color: 'white' }}>
      <h2>Access Document</h2>
      <p>Welcome, {userEmail || 'Guest'}!</p>
      <form onSubmit={handleAccessCheck} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <input
          type="number"
          placeholder="Enter Access ID"
          value={accessId}
          onChange={(e) => setAccessId(e.target.value)}
          required
          style={{
            padding: '10px',
            borderRadius: '6px',
            border: '1px solid #4b5563',
            backgroundColor: '#374151',
            color: 'white',
          }}
        />
        <button
          type="submit"
          style={{
            padding: '12px',
            backgroundColor: '#6366f1',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontWeight: '500',
          }}
        >
          Submit
        </button>
      </form>
      {message && <div style={{ marginTop: '10px', color: '#10b981' }}>{message}</div>}
    </div>
  );
};

export default AccessPage;
