import React, { useState, useEffect, useRef } from 'react';
import { useUser } from './UserContext';
import { Shield, Send, AlertTriangle, Info, CheckCircle, X, Activity, PieChart, ChevronRight, Menu, MessageSquare, User, Clock } from 'lucide-react';
import './Chatbot.css';
import ReactMarkdown from 'react-markdown';

const Home = () => {
  const { userEmail } = useUser();
  const [showChat, setShowChat] = useState(false);
  const [query, setQuery] = useState('');
  const [messages, setMessages] = useState([]);
  const [loadingResponse, setLoadingResponse] = useState(false);
  const [threatLevel, setThreatLevel] = useState('low');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeTool, setActiveTool] = useState('chat');
  const [notifications, setNotifications] = useState([
    { id: 1, text: 'Unusual login detected from IP 192.168.1.45', time: '2 hours ago', read: false },
    { id: 2, text: 'Security assessment complete', time: 'Yesterday', read: true },
    { id: 3, text: 'New security update available', time: '3 days ago', read: true }
  ]);
  
  const chatBodyRef = useRef(null);
  
  // Sample quick suggestions
  const quickSuggestions = [
    "What are common phishing techniques?",
    "How to create a strong password?",
    "Explain zero trust security",
    "Recent data breach incidents"
  ];

  // Extract department from email (assuming format: name_department@domain.com)
  const department = userEmail.split('@')[0].split('_')[1] || 'Unknown';
  
  // Sample threat data for visualization
  const threatData = {
    accessAttempts: 247,
    blockedThreats: 18,
    vulnerabilities: 3
  };
  
  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
  }, [messages]);
  
  // Initial welcome message
  useEffect(() => {
    setMessages([
      { 
        sender: 'bot', 
        text: `Welcome to SecureGuard! I'm your cybersecurity assistant. How can I help you today?`,
        timestamp: new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
      }
    ]);
  }, []);

  const handleSend = async () => {
    if (!query.trim()) return;

    const timestamp = new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
    const userMessage = { sender: 'user', text: query, timestamp };
    setMessages(prev => [...prev, userMessage]);
    setLoadingResponse(true);

    try {
      // Simulate network delay for demonstration
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const response = await fetch('http://localhost:7000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query })
      });

      const data = await response.json();
      const aiMessage = { 
        sender: 'bot', 
        text: data.response, 
        timestamp: new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
      };
      setMessages(prev => [...prev, aiMessage]);
      
      // Update threat level based on content (just for demonstration)
      if (query.toLowerCase().includes('breach') || query.toLowerCase().includes('attack')) {
        setThreatLevel('medium');
      } else if (query.toLowerCase().includes('hack') || query.toLowerCase().includes('vulnerability')) {
        setThreatLevel('high');
      }
    } catch (err) {
      setMessages(prev => [...prev, { 
        sender: 'bot', 
        text: 'Error contacting server. Please try again later.', 
        timestamp: new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
      }]);
    }

    setLoadingResponse(false);
    setQuery('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  const handlePrediction = () => {
    window.location.href = 'http://localhost:8501';
  };
  
  const handleQuickSuggestion = (suggestion) => {
    setQuery(suggestion);
    // Optional: auto-send the suggestion
    // setTimeout(() => handleSend(), 100);
  };
  
  const markAllAsRead = () => {
    setNotifications(notifications.map(notif => ({...notif, read: true})));
  };
  
  const renderToolContent = () => {
    switch (activeTool) {
      case 'chat':
        return (
          <div className="chat-section">
            <div className="chat-header">
              <div className="chat-header-title">
                <Shield size={20} />
                <span>SecureGuard AI Assistant</span>
              </div>
              <div className="threat-indicator">
                <span>Threat Level:</span>
                <span className={`threat-badge ${threatLevel}`}>{threatLevel.toUpperCase()}</span>
              </div>
            </div>
            
            <div className="chat-body" ref={chatBodyRef}>
              {messages.map((msg, index) => (
                <div key={index} className={`chat-message ${msg.sender}`}>
                  <div className="message-avatar">
                    {msg.sender === 'user' ? <User size={18} /> : <Shield size={18} />}
                  </div>
                  <div className="message-content">
                    <div className="message-header">
                      <span className="message-sender">{msg.sender === 'user' ? 'You' : 'SecureGuard'}</span>
                      <span className="message-time">{msg.timestamp}</span>
                    </div>
                    <div className="message-text">
                      {msg.sender === 'bot' ? (
                        <ReactMarkdown>{msg.text}</ReactMarkdown>
                      ) : (
                        msg.text
                      )}
                    </div>
                  </div>
                </div>
              ))}
              
              {loadingResponse && (
                <div className="chat-message bot loading">
                  <div className="message-avatar">
                    <Shield size={18} />
                  </div>
                  <div className="message-content">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
            </div>
            
            <div className="quick-suggestions">
              {quickSuggestions.map((suggestion, idx) => (
                <div 
                  key={idx} 
                  className="suggestion-pill"
                  onClick={() => handleQuickSuggestion(suggestion)}
                >
                  {suggestion}
                </div>
              ))}
            </div>
            
            <div className="chat-input">
              <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask a cybersecurity question..."
              />
              <button onClick={handleSend} disabled={loadingResponse || !query.trim()}>
                {loadingResponse ? (
                  <div className="button-loader"></div>
                ) : (
                  <Send size={18} />
                )}
              </button>
            </div>
          </div>
        );
      
      case 'analytics':
        return (
          <div className="analytics-section">
            <h2>Security Analytics Dashboard</h2>
            <div className="analytics-cards">
              <div className="analytics-card">
                <div className="analytics-icon">
                  <Activity size={24} />
                </div>
                <div className="analytics-content">
                  <div className="analytics-title">Access Attempts</div>
                  <div className="analytics-value">{threatData.accessAttempts}</div>
                  <div className="analytics-subtitle">Last 24 hours</div>
                </div>
              </div>
              
              <div className="analytics-card">
                <div className="analytics-icon blocked">
                  <AlertTriangle size={24} />
                </div>
                <div className="analytics-content">
                  <div className="analytics-title">Blocked Threats</div>
                  <div className="analytics-value">{threatData.blockedThreats}</div>
                  <div className="analytics-subtitle">Last 24 hours</div>
                </div>
              </div>
              
              <div className="analytics-card">
                <div className="analytics-icon vulnerable">
                  <Shield size={24} />
                </div>
                <div className="analytics-content">
                  <div className="analytics-title">Vulnerabilities</div>
                  <div className="analytics-value">{threatData.vulnerabilities}</div>
                  <div className="analytics-subtitle">Requires attention</div>
                </div>
              </div>
            </div>
            
            <div className="prediction-section">
              <h3>Breach Prediction</h3>
              <p>Based on current system behavior, our AI models can predict potential security issues.</p>
              <button className="prediction-button" onClick={handlePrediction}>
                Run Prediction Analysis
              </button>
            </div>
          </div>
        );
        
      case 'notifications':
        return (
          <div className="notifications-section">
            <div className="notifications-header">
              <h2>Notifications</h2>
              <button className="mark-read-button" onClick={markAllAsRead}>Mark all as read</button>
            </div>
            
            <div className="notifications-list">
              {notifications.length > 0 ? (
                notifications.map(notification => (
                  <div key={notification.id} className={`notification-item ${notification.read ? 'read' : 'unread'}`}>
                    <div className="notification-icon">
                      {notification.read ? <Info size={18} /> : <AlertTriangle size={18} />}
                    </div>
                    <div className="notification-content">
                      <div className="notification-text">{notification.text}</div>
                      <div className="notification-time">{notification.time}</div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-notifications">No new notifications</div>
              )}
            </div>
          </div>
        );
        
      default:
        return <div>Select a tool from the sidebar</div>;
    }
  };

  return (
    <div className="dashboard-container">
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <div className="logo">
            <Shield size={24} />
            <h2>SecureGuard</h2>
          </div>
          <button className="toggle-sidebar" onClick={() => setSidebarOpen(!sidebarOpen)}>
            <Menu size={20} />
          </button>
        </div>
        
        <div className="user-info">
          <div className="user-avatar">
            <User size={24} />
          </div>
          <div className="user-details">
            <div className="user-email">{userEmail}</div>
            <div className="user-department">{department} Department</div>
          </div>
        </div>
        
        <div className="sidebar-menu">
          <div 
            className={`menu-item ${activeTool === 'chat' ? 'active' : ''}`}
            onClick={() => setActiveTool('chat')}
          >
            <MessageSquare size={20} />
            <span>Security Chat</span>
          </div>
          
          <div 
            className={`menu-item ${activeTool === 'analytics' ? 'active' : ''}`}
            onClick={() => setActiveTool('analytics')}
          >
            <PieChart size={20} />
            <span>Analytics</span>
          </div>
          
          <div 
            className={`menu-item ${activeTool === 'notifications' ? 'active' : ''}`}
            onClick={() => setActiveTool('notifications')}
          >
            <Bell size={20} />
            <span>Notifications</span>
            {notifications.filter(n => !n.read).length > 0 && (
              <span className="notification-badge">
                {notifications.filter(n => !n.read).length}
              </span>
            )}
          </div>
        </div>
      </div>
      
      <div className="main-content">
        {renderToolContent()}
      </div>
    </div>
  );
};

export default Home;
