import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const AccessPage = () => {
  const { state } = useLocation();
  const [accessId, setAccessId] = useState('');
  const [message, setMessage] = useState('');
  const [deptInfo, setDeptInfo] = useState(null);

  const userEmail = state?.userEmail || '';

  // ‚úÖ Extract deptId from email: third part after dot (e.g., amar.gupta.1@x.com ‚Üí 1)
  const deptId = userEmail.split('.')[2]?.split('@')[0];

  console.log("‚úÖ Extracted deptId:", deptId);
  console.log("‚úÖ Received userEmail:", userEmail);

  useEffect(() => {
    const fetchDepartmentInfo = async () => {
      try {
        const response = await fetch(`http://localhost:5000/get-department-info/${deptId}`);
        const data = await response.json();

        if (data.success && data.departmentInfo) {
          console.log("‚úÖ Department Info from DB:", data.departmentInfo); // üëà full dept doc from DB
          setDeptInfo(data.departmentInfo);
        } else {
          console.warn("‚ö†Ô∏è No department found for this ID");
          setMessage(data.message || 'No department found.');
        }
      } catch (err) {
        console.error("‚ùå Error fetching department info:", err);
        setMessage('Server error while fetching department info.');
      }
    };

    if (deptId) fetchDepartmentInfo();
  }, [deptId]);

  const handleAccessCheck = (e) => {
    e.preventDefault();

    if (!deptInfo) {
      setMessage('Department information not loaded yet.');
      return;
    }

    const accessList = deptInfo.Access_ID;
    const accessNames = JSON.parse(deptInfo.Access.replace(/'/g, '"')); // üëà Convert string to array

    console.log("üîê Access ID Array:", accessList);
    console.log("üîê Access Name Array:", accessNames);
    console.log("üîç Entered Access ID:", accessId);

    const isAuthorized = accessList.includes(parseInt(accessId));

    if (isAuthorized) {
      const index = accessList.indexOf(parseInt(accessId));
      const resource = accessNames[index] || "Unknown Resource";

      setMessage(`‚úÖ Access granted to: ${resource}`);
    } else {
      setMessage('‚ùå Access denied: Invalid Access ID.');
    }
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#1f2937', color: 'white' }}>
      <h2>Access Document</h2>
      <p>Welcome, {userEmail || 'Guest'}!</p>
      <form onSubmit={handleAccessCheck} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <input
          type="number"
          placeholder="Enter Access ID"
          value={accessId}
          onChange={(e) => setAccessId(e.target.value)}
          required
          style={{
            padding: '10px',
            borderRadius: '6px',
            border: '1px solid #4b5563',
            backgroundColor: '#374151',
            color: 'white',
          }}
        />
        <button
          type="submit"
          style={{
            padding: '12px',
            backgroundColor: '#6366f1',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontWeight: '500',
          }}
        >
          Submit
        </button>
      </form>
      {message && <div style={{ marginTop: '10px', color: '#10b981' }}>{message}</div>}
    </div>
  );
};

export default AccessPage;
