import React, { useState } from 'react';

const AccessPage = () => {
  const [accessId, setAccessId] = useState('');
  const [message, setMessage] = useState('');

  const handleAccessCheck = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/access', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ accessId }),
      });

      const data = await response.json();
      setMessage(data.message); // Display response message
    } catch (err) {
      console.error(err);
      setMessage('Server error occurred.');
    }
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#1f2937', color: 'white' }}>
      <h2>Access Document</h2>
      <form onSubmit={handleAccessCheck} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <input
          type="number"
          placeholder="Enter Access ID"
          value={accessId}
          onChange={(e) => setAccessId(e.target.value)}
          required
          style={{
            padding: '10px',
            borderRadius: '6px',
            border: '1px solid #4b5563',
            backgroundColor: '#374151',
            color: 'white',
          }}
        />
        <button
          type="submit"
          style={{
            padding: '12px',
            backgroundColor: '#6366f1',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontWeight: '500',
          }}
        >
          Submit
        </button>
      </form>
      {message && <div style={{ marginTop: '10px', color: '#10b981' }}>{message}</div>}
    </div>
  );
};

export default AccessPage;
