import React, { useState } from 'react';
import { useUser } from './UserContext';
import './Chatbot.css'; // Create this CSS file for styling

const Home = () => {
  const { userEmail } = useUser();
  const [showChat, setShowChat] = useState(false);
  const [query, setQuery] = useState('');
  const [messages, setMessages] = useState([]);

  const handleSend = async () => {
    if (!query.trim()) return;
    
    const userMessage = { sender: 'user', text: query };
    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await fetch('http://localhost:7000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query })
      });

      const data = await response.json();
      const aiMessage = { sender: 'bot', text: data.response };
      setMessages(prev => [...prev, aiMessage]);
    } catch (err) {
      setMessages(prev => [...prev, { sender: 'bot', text: 'Error contacting server.' }]);
    }

    setQuery('');
  };

  return (
    <div>
      <h1>Welcome {userEmail}</h1>

      {/* Chat Icon */}
      <div className="chat-icon" onClick={() => setShowChat(!showChat)}>
        ðŸ’¬
      </div>

      {/* Chat Window */}
      {showChat && (
        <div className="chat-window">
          <div className="chat-header">Cybersecurity Chatbot</div>
          <div className="chat-body">
            {messages.map((msg, index) => (
              <div key={index} className={`chat-message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Ask a cybersecurity question..."
            />
            <button onClick={handleSend}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
