// Home.js
import React, { useEffect, useState } from 'react';
import { useUser } from './UserContext'; // Import the context

const Home = () => {
  const { userEmail } = useUser(); // Access the email from context
  const [employeeData, setEmployeeData] = useState(null);

  useEffect(() => {
    if (userEmail) {
      fetch('http://localhost:5000/get-employee-details', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: userEmail }),
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.employee) {
            console.log("Employee Data:", data.employee);
            setEmployeeData(data.employee);
          } else {
            console.warn("No employee data found.");
          }
        })
        .catch((err) => console.error("Error fetching employee data:", err));
    }
  }, [userEmail]);

  return (
    <div>
      <h1>Welcome {userEmail}</h1>
      {employeeData && (
        <div>
          <p><strong>Name:</strong> {employeeData.Emp_name}</p>
          <p><strong>Department:</strong> {employeeData.Dept_Name}</p>
          <p><strong>Access IDs:</strong> {employeeData.Access_ID.join(', ')}</p>
          <p><strong>Access:</strong> {employeeData.Access.join(', ')}</p>
        </div>
      )}
    </div>
  );
};

export default Home;
