import React from 'react';
import { useHistory } from 'react-router-dom'; // To navigate to another route
import { useUser } from './UserContext'; // Import the context

const Home = () => {
  const { userEmail } = useUser(); // Access the email from context
  const history = useHistory(); // Initialize the history object for navigation

  const handleAccessDocument = () => {
    // Navigate to the /access page, passing the userEmail as state
    history.push({
      pathname: '/access',
      state: { userEmail } // Pass the user email as state to the /access route
    });
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#1f2937', color: 'white' }}>
      <h1>Welcome {userEmail}</h1>
      
      <button
        onClick={handleAccessDocument}
        style={{
          padding: '12px',
          backgroundColor: '#6366f1',
          color: 'white',
          border: 'none',
          borderRadius: '6px',
          fontWeight: '500',
          cursor: 'pointer',
          marginTop: '20px',
        }}
      >
        Access Document
      </button>
    </div>
  );
};

export default Home;
