import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom'; // To access the state passed from Home

const AccessPage = () => {
  const { state } = useLocation(); // Get the state passed from the Home component
  const [accessId, setAccessId] = useState('');
  const [message, setMessage] = useState('');
  const [departmentInfo, setDepartmentInfo] = useState(null);
  const [isAccessGranted, setIsAccessGranted] = useState(false);

  // Access the userEmail passed through state
  const userEmail = state?.userEmail;

  // Get the departmentId from the logged-in user (you can store this in context if needed)
  const deptId = state?.deptId; // Assuming the deptId is also passed along

  // Fetch department data and access control info when the component mounts
  useEffect(() => {
    if (deptId) {
      // Fetch the department and access info from the backend based on the deptId
      fetch(`http://localhost:5000/get-department-info/${deptId}`)
        .then((response) => response.json())
        .then((data) => {
          if (data && data.success) {
            setDepartmentInfo(data.departmentInfo);
          } else {
            setMessage('Unable to fetch department information.');
          }
        })
        .catch((err) => {
          console.error(err);
          setMessage('Error fetching department information.');
        });
    }
  }, [deptId]);

  const handleAccessCheck = async (e) => {
    e.preventDefault();

    if (!departmentInfo) {
      setMessage('Department information not loaded yet.');
      return;
    }

    // Check if the accessId exists in the Access_ID array for the department
    if (departmentInfo.Access_ID.includes(parseInt(accessId))) {
      setIsAccessGranted(true);
      setMessage('Valid user, access granted.');
    } else {
      setIsAccessGranted(false);
      setMessage('Access denied. Invalid Access ID.');
    }
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#1f2937', color: 'white' }}>
      <h2>Access Document</h2>
      <p>Welcome, {userEmail ? userEmail : 'Guest'}!</p> {/* Display the userEmail */}

      {/* Display department info if available */}
      {departmentInfo && (
        <div>
          <p>Department: {departmentInfo.Dept_Name}</p>
        </div>
      )}

      <form onSubmit={handleAccessCheck} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <input
          type="number"
          placeholder="Enter Access ID"
          value={accessId}
          onChange={(e) => setAccessId(e.target.value)}
          required
          style={{
            padding: '10px',
            borderRadius: '6px',
            border: '1px solid #4b5563',
            backgroundColor: '#374151',
            color: 'white',
          }}
        />
        <button
          type="submit"
          style={{
            padding: '12px',
            backgroundColor: '#6366f1',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontWeight: '500',
          }}
        >
          Submit
        </button>
      </form>

      {message && <div style={{ marginTop: '10px', color: isAccessGranted ? '#10b981' : '#f87171' }}>{message}</div>}
    </div>
  );
};

export default AccessPage;
