import React, { useState, useEffect } from 'react';
import { Cpu, Brain, Zap, Database, ArrowRight } from 'lucide-react';

const Landing = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [activeFeature, setActiveFeature] = useState(0);

  useEffect(() => {
    setIsLoaded(true);
    
    const interval = setInterval(() => {
      setActiveFeature(prev => (prev + 1) % 4);
    }, 3000);
    
    return () => clearInterval(interval);
  }, []);

  const features = [
    {
      icon: <Brain className="text-purple-500" />,
      title: "Neural Networks",
      description: "Advanced deep learning algorithms that mimic human brain functionality"
    },
    {
      icon: <Cpu className="text-blue-500" />,
      title: "Optimized Processing",
      description: "Efficient computation designed for complex AI tasks and real-time inference"
    },
    {
      icon: <Database className="text-green-500" />,
      title: "Data Intelligence",
      description: "Transform raw data into actionable insights with our smart analytics"
    },
    {
      icon: <Zap className="text-yellow-500" />,
      title: "Lightning Fast",
      description: "Accelerated model training and deployment for rapid implementation"
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-gray-900 text-white overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {[...Array(20)].map((_, i) => (
          <div 
            key={i}
            className={`absolute rounded-full opacity-10 bg-white
              ${isLoaded ? 'animate-pulse' : ''}`}
            style={{
              width: `${Math.random() * 10 + 5}rem`,
              height: `${Math.random() * 10 + 5}rem`,
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${Math.random() * 10 + 5}s`
            }}
          />
        ))}
      </div>

      {/* Navigation */}
      <nav className={`relative px-6 py-4 flex justify-between items-center transition-all duration-1000 ${isLoaded ? 'opacity-100' : 'opacity-0'}`}>
        <div className="flex items-center">
          <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-blue-500 rounded-lg flex items-center justify-center mr-3">
            <Cpu size={24} />
          </div>
          <span className="text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
            NeuralFusion
          </span>
        </div>
        
        <div className="hidden md:flex space-x-6">
          <a href="#" className="hover:text-purple-300 transition-colors">Features</a>
          <a href="#" className="hover:text-purple-300 transition-colors">Technology</a>
          <a href="#" className="hover:text-purple-300 transition-colors">Case Studies</a>
          <a href="#" className="hover:text-purple-300 transition-colors">About</a>
        </div>
        
        <button className="bg-gradient-to-r from-purple-500 to-blue-500 px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
          Get Started
        </button>
      </nav>

      {/* Hero Section */}
      <div className="relative px-6 md:px-12 py-12 md:py-24 max-w-7xl mx-auto">
        <div className="grid md:grid-cols-2 gap-12 items-center">
          <div className={`transition-all duration-1000 delay-300 transform ${isLoaded ? 'translate-x-0 opacity-100' : '-translate-x-12 opacity-0'}`}>
            <h1 className="text-4xl md:text-6xl font-bold mb-6 leading-tight">
              <span className="bg-clip-text text-transparent bg-gradient-to-r from-purple-400 via-pink-500 to-blue-400">
                Next Generation
              </span>
              <br />AI Solutions
            </h1>
            <p className="text-lg md:text-xl text-gray-300 mb-8">
              Harnessing the power of advanced machine learning to transform 
              how you interact with technology and data.
            </p>
            <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4">
              <button className="bg-gradient-to-r from-purple-500 to-blue-500 px-6 py-3 rounded-lg hover:opacity-90 transition-opacity font-medium flex items-center justify-center">
                Explore Solutions
                <ArrowRight size={18} className="ml-2" />
              </button>
              <button className="border border-purple-400 px-6 py-3 rounded-lg hover:bg-purple-900/30 transition-all font-medium">
                Watch Demo
              </button>
            </div>
          </div>
          
          {/* Abstract 3D Visual */}
          <div className={`transition-all duration-1000 delay-500 transform ${isLoaded ? 'translate-x-0 opacity-100' : 'translate-x-12 opacity-0'}`}>
            <div className="relative h-64 md:h-96 w-full flex items-center justify-center">
              {/* Central Sphere */}
              <div className="absolute w-32 h-32 bg-gradient-to-br from-purple-400 to-blue-500 rounded-full animate-pulse" />
              
              {/* Orbiting Elements */}
              {[...Array(3)].map((_, i) => (
                <div 
                  key={i}
                  className="absolute w-full h-full animate-spin"
                  style={{ 
                    animationDuration: `${20 + i * 5}s`,
                    transform: `rotate(${i * 40}deg)`
                  }}
                >
                  <div className="absolute w-12 h-12 bg-gradient-to-br from-pink-500 to-purple-500 rounded-full top-0 left-1/2 transform -translate-x-1/2" />
                </div>
              ))}
              
              {/* Connection Lines */}
              <div className="absolute inset-0 w-full h-full rounded-full border-4 border-purple-500/20 animate-ping" style={{ animationDuration: '3s' }} />
              <div className="absolute inset-0 w-3/4 h-3/4 rounded-full border-2 border-blue-400/30 animate-ping" style={{ animationDuration: '4s' }} />
              <div className="absolute inset-0 w-1/2 h-1/2 rounded-full border border-pink-400/40 animate-ping" style={{ animationDuration: '5s' }} />
            </div>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="relative px-6 md:px-12 py-16 bg-black/30 backdrop-blur-sm">
        <div className="max-w-7xl mx-auto">
          <h2 className={`text-3xl md:text-4xl font-bold mb-12 text-center transition-all duration-1000 delay-700 transform ${isLoaded ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'}`}>
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-400 to-purple-400">
              Cutting-Edge AI Features
            </span>
          </h2>
          
          <div className="grid md:grid-cols-4 gap-6">
            {features.map((feature, index) => (
              <div 
                key={index}
                className={`bg-gradient-to-b from-gray-800/60 to-gray-900/60 backdrop-blur-sm p-6 rounded-xl border border-gray-700 transition-all duration-500 transform 
                  ${isLoaded ? 'translate-y-0 opacity-100' : 'translate-y-12 opacity-0'} 
                  ${activeFeature === index ? 'scale-105 shadow-xl shadow-purple-500/20 border-purple-500/50' : 'scale-100'}`}
                style={{ transitionDelay: `${700 + index * 100}ms` }}
              >
                <div className="w-12 h-12 mb-4 flex items-center justify-center">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                <p className="text-gray-400">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* CTA Section */}
      <div className="relative px-6 md:px-12 py-20 max-w-7xl mx-auto">
        <div className={`bg-gradient-to-r from-purple-900/50 to-blue-900/50 rounded-2xl p-8 md:p-12 text-center backdrop-blur-sm border border-gray-700 transition-all duration-1000 delay-1000 ${isLoaded ? 'opacity-100 scale-100' : 'opacity-0 scale-95'}`}>
          <h2 className="text-3xl md:text-4xl font-bold mb-4">Ready to Transform Your AI Capabilities?</h2>
          <p className="text-lg text-gray-300 mb-8 max-w-3xl mx-auto">
            Join the future of intelligent technology. Our platform offers seamless integration, 
            powerful analytics, and cutting-edge machine learning tools.
          </p>
          <button className="bg-gradient-to-r from-purple-500 to-blue-500 px-8 py-4 rounded-lg text-lg font-medium hover:opacity-90 transition-opacity">
            Start Your Journey
          </button>
        </div>
      </div>
    </div>
  );
};

export default Landing;
